ruleExplore.bookList:
@js:
var list = java.getElements('#m-pl-container li');
var data = [];
for (i = 0; i < list.length; i++) {
	name = list[i].select('p.dec a').text();
	author = list[i].select('a.s-fc3').text();
	kind = list[i].select('span.nb').text();	
	cover = String(list[i].select('img.j-flag').attr('src')).split("?")[0];
	intro = list[i].select('p.dec a').text();
	url = list[i].select('p.dec a').attr('href');
	data.push({
		'name': name,
		'author': author,
		'kind': kind,
		'intro': intro,
		'cover': cover,
		'url': url
	});
}
