Finished = `{{get('finished',Get('o'))}}`;
P = `{{Get('p')}}`;
Q = `{{Get('q')}}`;
R = `{{Get('r')}}`;
Person = `{{get('person',Get('s'))}}`;
Porn = `{{get('porn',Get('t'))}}`;
Orientation = `{{get('orientation',Get('u'))}}`;
Period = `{{get('period',Get('v'))}}`;
Score = `{{get('score',Get('w'))}}`;
$ = (x) => `{{Url("源站：") + Path}}/search?${x}&orderType=1&page={{page}}&searchType=1&space=${P}&source=${Q}&finished=${Finished}&orderType=${R}&person=${Person}&porn=${Porn}&orientation=${Orientation}&period=${Period}&score=${Score}&size=36`;
u = Url("源站：") + Path + '/';
["最新上架", "最近更新", "最多观看", "最多收藏"].map((p, q) => {
    push(p, `{{Url("源站：") + Path}}/search?page={{page}}&size=20&orderType=${q + 1}`, 0.4);
});
push("【 排行 】", null, 1);
["周榜", "月榜", "年榜", "总榜"].map((p, q) => {
    push(p, `{{Url("源站：") + Path}}/rank?type=${q + 1}`, null);
});
[["分类", "category"],["标签", "includeTags"]].map([a, b] => {
    push(`【 ${a} 】`, '', 1);
    if (/分类/.test(a)) push(`全部`, $(""), null);
    d = /y/.test(b) ? "categories": "tags";
    url = u + 'app/novel/' + d + '?type=0';
    res = java.ajax(url);
    if(res.match(/no-js/)){
        java.webView('',Url("源站："),'');
        res=java.ajax(url);
    }
    c = JSON.parse(res).model;
    for (i = 0; i < c.length; i++) {
        push(
            c[i].name,
            $(b + "=" + encodeURIComponent(c[i].name)),
            null
        );
    }
    if (/分类/.test(a)) push(`贤者`, $(b + "=" + encodeURIComponent('贤者小说')), null);
});

JSON.stringify(sort);

