jsLib:
// #region url
function urlCoverUrl(url) {
  return `${url},{"headers": {"Referer":"https://www.pixiv.net/"}}`
}

function urlNovelDetailed(nid) {
  return `https://www.pixiv.net/ajax/novel/${nid}`
}

function urlSeriesDetailed(sid) {
  return `https://www.pixiv.net/ajax/novel/series/${sid}`
}

function urlSeriesNovels(sid, limit, offset) {
  if (limit > 30) {
    limit = 30
  }

  if (limit < 10) {
    limit = 10
  }

  return `https://www.pixiv.net/ajax/novel/series_content/${sid}?limit=${limit}&last_order=${offset}&order_by=asc&lang=zh`
}

function cacheGetAndSet(cache, key, supplyFunc) {
  let v = cache.get(key)
  if (v === undefined || v === null) {
    v = JSON.stringify(supplyFunc())
    // 缓存10分钟
    cache.put(key, v, 600)
  }
  return JSON.parse(v)
}

// #endregion

// type novel
// id -> novel id
// seriesId -> series id (仅当 series 存在时)
// seriesTitle -> series title

// type novelSeries
// id -> series id
// title -> series title
// latestEpisodeId -> 最新章 id
// 特殊情况: 当 isOneshot true 时， 实际上 id 已失效， 需要直接用 novelId

function combineNovels(seriesSet, novels) {
  return novels.filter(novel => {
    if (novel.novelType === "novel") {
      //单本直接解析为一本书
      if (novel.seriesId === undefined || novel.seriesId === null) {
        return true
      }

      //集合中没有该系列解析为一本书
      if (!seriesSet.has(novel.seriesId)) {
        seriesSet.add(novel.seriesId)
        return true
      }
    } else if (novel.novelType === "series") {
      if (!seriesSet.has(novel.id)) {
        seriesSet.add(novel.id)
        return true
      }
    }

    return false
  })
}

function formatNovels(novels) {
  const newNovels = novels.map(novel => {
    const isSeries = novel.novelType === "series"
    const isNovelWithinSeries = !(novel.seriesId === undefined || novel.seriesId === null)

    // INIT TAG
    if (novel.tags === undefined || novel.tags === null) {
      novel.tags = []
    }

    if (isSeries) {
      novel.title = novel.title
      novel.author = novel.userName
      novel.tags.unshift(novel.isOneshot ? "短篇" : "长篇")
      novel.textCount = novel.textLength
      novel.description = novel.caption
      novel.detailedUrl = novel.isOneshot ? urlNovelDetailed(novel.novelId) : urlSeriesDetailed(novel.id)
      novel.coverUrl = novel.cover && novel.cover.urls && urlCoverUrl(novel.cover.urls["480mw"])
    } else if (isNovelWithinSeries) {
      novel.title = novel.seriesTitle
      // novel.title = `${novel.seriesTitle}: ${novel.title}`
      novel.author = novel.userName
      novel.tags.unshift("长篇")
      novel.textCount = novel.textCount
      novel.description = novel.description
      novel.detailedUrl = urlSeriesDetailed(novel.seriesId)
      novel.coverUrl = urlCoverUrl(novel.url)
    } else {
      novel.title = novel.title
      novel.author = novel.userName
      novel.tags.unshift("短篇")
      novel.textCount = novel.textCount
      novel.description = novel.description
      novel.detailedUrl = urlNovelDetailed(novel.id)
      novel.coverUrl = urlCoverUrl(novel.url)
    }

    novel.tags = novel.tags.join(",")
    return novel
  })
  return newNovels
}

function handlerNoLogin() {
  return () => {
    java.longToast("此功能需要在书源登录后才能使用")
    return []
  }
}

