var seriesSet = new Set();

function handlerFactory() {
  let cookie = String(java.getCookie("https://www.pixiv.net/", null))
  if (cookie === null || cookie === undefined || cookie === "") {
    return handlerNoLogin()
  }

  if (baseUrl.indexOf("/bookmark") !== -1) {
    return handlerBookMarks()
  }

  return handlerRecommend()
}

function handlerRecommend() {
  return () => {
    const res = JSON.parse(result)
    const novels = res.body.thumbnails.novel.map((novel) => {
      novel.novelType = "novel"
      return novel
    })
    const series = res.body.thumbnails.novelSeries.map((novel) => {
      novel.novelType = "series"
      return novel
    })

    return formatNovels(combineNovels(seriesSet, series.concat(novels)))
  }
}

function handlerBookMarks() {
  return () => {
    const novels = JSON.parse(result).body.works.map((novel) => {
      novel.novelType = "novel"
      return novel
    })
    if (novels === undefined || novels.length === 0) {
      return []
    }
    return formatNovels(combineNovels(seriesSet, novels))
  }
}

(() => {
  return handlerFactory()()
})()

