@js:

// 完全匹配用户名
function urlSearchUser(username) {
  return `https://www.pixiv.net/ajax/search/users?nick=${encodeURI(username)}&s_mode=s_usr&i=1`
}

function urlUserAllWorks(uid) {
  return `https://www.pixiv.net/ajax/user/${uid}/profile/all?lang=zh`
}

function urlUserNovels(uid, nidList) {
  return `https://www.pixiv.net/ajax/user/${uid}/novels?${nidList.map(v => "ids[]=" + v).join("&")}`
}

function urlSearchSeriesNovels(seriesName) {
  return `https://www.pixiv.net/ajax/search/novels/${encodeURI(seriesName)}?word=${encodeURI(seriesName)}&order=date_d&mode=all&p=1&s_mode=s_tag&gs=1&lang=zh`
}

var first = true;

// 存储seriesID
var seriesSet = {
  keywords: "Pixiv:Search",
  has: (value) => {
    let page = Number(java.get("page"))
    if (page === 1 && first) {
      first = false
      cache.deleteMemory(this.keywords)
      return false
    }

    let v = cache.getFromMemory(this.keywords)
    if (v === undefined || v === null) {
      return false
    }
    let set = new Set(JSON.parse(v))
    return set.has(value)
  },

  add: (value) => {
    let v = cache.getFromMemory(this.keywords)
    if (v === undefined || v === null) {
      cache.putMemory(this.keywords, JSON.stringify([value]))

    } else {
      let arr = JSON.parse(v)
      if (typeof arr === "string") {
        arr = Array(arr)
      }
      arr.push(value)
      cache.putMemory(this.keywords, JSON.stringify(arr))
    }
  },
};

function getAjaxJson(url) {
  return cacheGetAndSet(cache, url, () => {
    return JSON.parse(java.ajax(url))
  })
}

function getWebviewJson(url, parseFunc) {
  return cacheGetAndSet(cache, url, () => {
    let html = java.webView(null, url, null)
    return JSON.parse(parseFunc(html))
  })
}

function isLogin() {
  let cookie = String(java.getCookie("https://www.pixiv.net/", null))
  return typeof cookie === "string" && cookie !== ""
}

function getUserNovels(username) {
  if (!isLogin()) {
    return []
  }

  const uidList = getAjaxJson(urlSearchUser(username)).body.page.userIds

  // 仅限3个作者
  if (uidList.length >= 3) {
    uidList.length = 3
  }

  let novels = []
  let page = Number(java.get("page"))

  uidList.forEach(id => {
    let r = getAjaxJson(urlUserAllWorks(id))
    let novelsId = Object.keys(r.body.novels).reverse().slice((page - 1) * 20, page * 20)
    let url = urlUserNovels(id, novelsId)
    // java.log(`发送的Ajax请求:${url}`)
    let userNovels = getWebviewJson(url, html => {
      return (html.match(new RegExp(">\\{.*?}<"))[0].replace(">", "").replace("<", ""))
    }).body
    java.log(JSON.stringify(userNovels, 2))
    novels = novels.concat(Object.values(userNovels))
  })

  return novels
}

function getSeriesNovels(seriesName) {
  if (!isLogin()) {
    return []
  }

  const resp = getAjaxJson(urlSearchSeriesNovels(seriesName))
  const novels = resp.body.novel.data

  if (novels === null || novels.length === 0) {
    return []
  }

  return novels
}

(() => {
  // 支持翻页的小说查询 / 一页用户 / 一页系列
  const searchKey = String(java.get("key"))

  let novelsList = []

  novelsList = novelsList.concat(getSeriesNovels(searchKey))
  novelsList = novelsList.concat(getUserNovels(searchKey))
  let res = JSON.parse(result);
  novelsList = novelsList.concat(res.body.novel.data)

  novelsList.map((novel) => {
    if (novel.latestEpisodeId) {
      novel.novelType = "series"
    } else {
      novel.novelType = "novel"
    }
    return novel
  })
  return formatNovels(combineNovels(seriesSet, novelsList))
})();

ruleToc.chapterList:
@js:

function seriesHandler(res) {
    const limit = 30
    let returnList = [];
    let seriesID = res.seriesNavData.seriesId
    let allChaptersCount = (() => {
        let result = cacheGetAndSet(cache, urlSeriesDetailed(seriesID), () => {
            return JSON.parse(java.ajax(urlSeriesDetailed(seriesID)))
        }).body.total
        return result;
    })();

    //发送请求获得相应数量的目录列表
    function sendAjaxForGetChapters(lastIndex) {
        let url = urlSeriesNovels(seriesID, limit, lastIndex)
        res = cacheGetAndSet(cache, url, () => {
            return JSON.parse(java.ajax(url))
        })
        res = res.body.page.seriesContents
        res.forEach(v => {
            v.chapterUrl = urlNovelDetailed(v.id)
        })
        return res;
    }

    //逻辑控制者 也就是使用上面定义的两个函数来做对应功能
    //要爬取的总次数
    let max = (allChaptersCount / limit) + 1
    for (let i = 0; i < max; i++) {
        //java.log("i的值:"+i)
        let list = sendAjaxForGetChapters(i * limit);
        //取出每个值
        returnList = returnList.concat(list)
    }
    return returnList
}

function aloneHandler() {
    return [{title: book.name, chapterUrl: baseUrl}]
}

(() => {
    let res = JSON.parse(result).body
    if (res.seriesNavData === null || res.seriesNavData === undefined) {
        return aloneHandler()
    }
    return seriesHandler(res)
})()

