var bv=book.getVariable("custom");
if(!bv||bv==""){
	//java.log("未设置书源接口，默认1")
	v=1-1;
	}else{
		v=Number(bv)-1;
		}
//	java.put("v",v)
//result;
j=org.jsoup.Jsoup.parse(result);
$=e=>j.select(e);
dec=u=>decodeURIComponent(u);

intro=v=>{
	var lis=$("li.site"),list=[],
	txt=lis.get(v).select("a:eq(1)").text(),
	h=lis.get(v).select("a:eq(1)").attr("href");
	curr=`\n当前源：${v+1}
	\n章节目录来自：${txt}\n
	${dec(h).split("?u=")[1]}`;
	lisa=lis.toArray();
	lb=lisa.forEach((a,i)=>{
		name=a.select("a:eq(1)").text()
		u=a.select("a:eq(1)").attr("href")
		url=dec(u).split("?u=")[1];
		add=`${i+1}、${name}：${url}`;
		list.push(add)
		})
		tips="\n源列表：（多个源可设置书籍变量更改目录）\n"
		jie="\n简介：\n"+$("#intro").text();
		jsa=curr+tips+list.join("\n")+jie;
		return jsa;
		}

link=v=>{
	var u=$("li.site").get(v)
	.select("a:eq(1)").attr("href");
	return dec(u).split("?u=")[1];
	}

(function(){
	name=$("p.title").text();
	author=$("p.author").text();
	kind=$("p[class~=category|time]").text()
	last=$("p.newestChapter a").text()
	cover=$(".BGsectionOne-top-left img").attr("src");
	info={
		n:name,
		a:author,
		k:kind,
		l:last,
		i:intro(v),
		c:cover,
		u:link(v)
		};
	return info;
	})()

ruleContent.content:
@js:
try {
	var d = (a, b) => {
		b = java.md5Encode(b);
   var d = b.substring(0, 16);
   var e = b.substring(16);
   type = "AES/CBC/PKCS7Padding";
  cipher = java.createSymmetricCrypto(
  	type, e, d);
   	return cipher.decryptStr(a);
   }
   code = src.match(/html\((d\(.+\))\)/)[1];
   //java.log(code)
		result = eval(code);
	} catch(err) {
		java.log(err)
		//如果解密出错，使用动态解密
		web = java.webView(null,baseUrl,null);
		java.setContent(web);
		content = java.getElement(
			".RBGsectionThree-content");
			result = content.html();
		} finally {
			result
			}

