let elements = java.getElements("@css:.l-box .pure-g[id] .pure-u-1-1");
if (elements.size() == 0) {
    elements = java.getElements("@css:.l-box div.pure-g  .pure-u-1-1");
    let array = [];
    for(let i = 0; i < elements.size(); i++) {
