loginUrl:
eval(String(source.bookSourceComment));
function Login() {
    Account = result['账号：'] ? result['账号：'] : source.getLoginInfoMap()["账号："];
    Password = result['密码：'] ? result['密码：'] : source.getLoginInfoMap()["密码："];
    if (Account == '' && Password == '') {
        login('【登录提示】' + '\n\n请输入账号和密码');
    } else if (Account == '') {
        login('【登录提示】' + '\n\n请输入账号');
    } else if (Password == '') {
        login('【登录提示】' + '\n\n请输入密码');
    } else {
        //请求路径：/auth/sign-in
        var pathname = 'auth/sign-in';
        //请求方式：POST
        var method = "POST";
        //请求体：{"email":"274978887","password":"sq123456789,."}
        var requestBody = '{"email":"' + Account + '","password":"' + Password + '"}';
        //请求头：使用源注释辅助生成
        var signature = getsignature(pathname, setTime, method);
        var headers = "\{\"time\":\"" + setTime + "\",\"Content-Type\":\"application/json; charset=UTF-8\",\"nonce\":\"" + nonce + "\", \"signature\":\"" + signature + "\",\"Accept\":\"application/vnd.picacomic.com.v1+json\",\"app-uuid\":\"webUUID\"\}";
        var option = {
            "method": String(method),
            "body": String(requestBody),
            "headers": String(headers)
        };
        Response = java.ajax('https://api.go2778.com/auth/sign-in,' + JSON.stringify(option));
        if (JSON.parse(Response).code == 200) {
            let jsondata = JSON.parse(Response);
            let token = jsondata.data.token;
            cache.put("authorization", token);
            java.log(Response);
            login('【登录提示】\n\n┏┅━┅━┅━┅━┅┅━┅━┅━┅━┅┓\n┋┋' + '\n' + Account + '\n┋┋' + '\n❤️登录成功❤️\n┋┋' + '\n┗┅━┅━┅━┅━┅┅━┅━┅━┅━┅┛');
        } else {
            java.log(Response);
            login('【登录提示】' + '\n' + Account + '\n♣️登录失败♣️\n\n请检查账号和密码');
        }
    }
}

function login(e) {
    if (e == undefined) {
        return;
    }
    java.longToast(e);
}

