injectJs:
//选择需要删除的标签，以,分隔
items = document.querySelectorAll(`
	.notice-top,
	.navbar-nav>li:nth-child(n+3),
	.navbar-right,
	.anti-select,
	#show-qrcode,
	#ex-comment,
	.hidden-more
`)

//把选择的html值改成空
Array.from(items,(item)=>{
	item.innerHTML = ``
	item.style.display = `none`
})



//上面是通用js，下面的不是
//删除网站的随机广告
items = document.querySelectorAll('.item')
if(items){
Array.from(items,(item)=>{
if(item.querySelector('.item-num').innerText == 'AD'){
		item.innerHTML = ``
		item.style.display = `none`
	}
})
}
